#define pixelPerBlock 36
__global__ void maxFilter2D_opt(const float* data_in, int m, int n, float *out_idx) {
    __shared__ float sbuffer[pixelPerBlock][10];
    int tid  = threadIdx.x;
    int pixel_id = tid / 10;

    int cx = blockIdx.x;
    int cy = blockIdx.y + pixel_id;

    int ty = tid % 10;

    if (tid < pixelPerBlock*10) {
        int curr_x, curr_y;
        if (ty < 3) {
            curr_x = cx - 1;
            curr_y = cy - 1 + ty;
        } else if (ty < 6) {
            curr_x = cx;
            curr_y = cy - 1 + (ty - 3);
        } else if (ty < 9) {
            curr_x = cx + 1;
            curr_y = cy - 1 + (ty - 6);
        }
        if (curr_x >= 0 && curr_y >= 0 && curr_x < m && curr_y < n) {
            sbuffer[pixel_id][ty] = data_in[curr_x * n + curr_y];
        } else {
            sbuffer[pixel_id][ty] = -FLT_MAX;
        }

        if (ty == 9) {
            sbuffer[pixel_id][9] = cx * n + cy;
        }
        __syncwarp();

        if (blockIdx.x == 0 && blockIdx.y == 0 && ty == 0 && pixel_id == 0) {
            for (int j=0;j<10;++j) {
                printf("tid: %d, sbuffer[%d][%d]: %f\n", tid, pixel_id, j, sbuffer[pixel_id][j]);
            }
            
        }
    }
    

    

